import axios from 'axios'
import React, { Component } from 'react'
import { format } from "timeago.js";
import {Link} from 'react-router-dom'
import CreateNote from 'CreateNote.js'

export default class NotesList extends Component {
  state = {
    notes : []
  }

  async componentDidMount(){
   this.getNotes()
  }

  async getNotes(){
     const res = await axios.get('http://localhost:4000/api/notes')
    this.setState({
      notes : res.data
    })
  }

  deleteNote = async (id) => {
    await axios.delete('http://localhost:4000/api/notes/' + id)
    this.getNotes()
  }

  render() {
    return (
      <div className="row">
        {
          this.state.notes.map(note => (
            <div key={note._id} className="col-md-4 p-2">
              <div className="card">
              <div className="card-header d-flex justify-content-between">
                <h5 className="text-success">{note.title}</h5>
                <Link className="btn btn-secondary" to="/edit">Editar</Link>
              </div>
                <div className="card-body">
                  <p>{note.content}</p>
                  <p>Autor: {note.author}</p>
                  <p>{format(note.date)}</p>
                </div>
                <div className="card-footer">
                  <button className="btn btn-danger" onClick={() => this.deleteNote(note._id)}>Delete</button>
                </div>
              </div>
            </div>
          ))
        }
      </div>
      //libreria TIMEAGO JS
    )
  }
}
