import React, { Component } from 'react'
import axios from 'axios'

export default class CreateUser extends Component {
  state = {
    users : [],
    username : ''
  }

  componentDidMount(){
    //Axios es para hacer la peticion GET y obtener los usuarios
    this.getUsers()
    console.log(this.state.users)
  }

  getUsers = async () => {
    const res = await axios.get('http://localhost:4000/api/users');
    this.setState({users : res.data})
  }

  onChangeUserName = (e) => {
    this.setState({
      username : e.target.value
    })
  }

  onSubmit = async e => {
    //Cancelar el conportamiento por defecto del form
    e.preventDefault(); 
    // Mandar los datos con el metodo POST de Axios
    await axios.post('http://localhost:4000/api/users', {
      username : this.state.username
    })
    this.getUsers()
    this.setState({username : ""})
  }

  deleteUser = async (id) => {
    await axios.delete('http://localhost:4000/api/users/' + id)
    this.getUsers()
  }

  render() {
    return (
        <div className="row">
          <div className="col-md4">
            <div className="card card-body">
              <h3>Create New User</h3>
              <form onSubmit={this.onSubmit}>
                <div className="form-group">
                  <input 
                    type="text" 
                    className="form-control" 
                    value={this.state.username}
                    onChange={this.onChangeUserName}
                    placeholder="Nombre de usuario"
                  />
                  <button type="submit" className="btn btn-block btn-outline-info mt-2">Guardar</button>
                </div>
              </form>
            </div>
          </div>
          <div className="col-md-8">
              <ul className="list-group">
                {
                  this.state.users.map(user => (
                    <li 

                      className="list-group-item list-group-item-action" 
                      key={user._id}
                      onDoubleClick={() => this.deleteUser(user._id)}
                      > 
                      {user.username}
                    </li>))
                }
              </ul>
          </div>
